import { Component, OnInit } from '@angular/core';
import { Producto } from '../list-view/list-view.component';
import { ProductosService } from '../Services/productos.service';
import { Router } from '@angular/router';
import { Renderer2 } from '@angular/core';
import { Modal } from 'bootstrap';
import { StatusModalService } from '../Services/status-modal.service';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent implements OnInit {

  addProductRequest: Producto = {
    prodId: 0,
    prodDescription: '',
    prodPrice: 0,
    prodStock: 0,
    prodSaleUnit: ''
  }

  constructor(private productoService: ProductosService, private router: Router, private Modal:StatusModalService) { }

  ngOnInit(): void {
  }

  addProduct()
 {
  this.productoService.addProduct(this.addProductRequest)
  .subscribe({
    next: (producto) => {
      this.router.navigate(['/listview'])
      this.Modal.openModalasync();


    }, error: (err) => 
    console.log(err)
  })
 }
}
<h1>Agregar producto a inventario</h1>
<form #form="ngForm" (ngSubmit)="addProduct()">
    <div class="mb-3">
        <label for="inputId" class="form-label">
            ID del producto
        </label>
        <input type="number" class="form-control" id="inputId"
        name="Id" [(ngModel)]="addProductRequest.prodId" >
    </div>
    <div class="mb-3">
        <label for="inputDesc" class="form-label">
            Descripcion del producto
        </label>
        <input type="text" class="form-control" id="inputDesc"
        name="Description"  [(ngModel)]="addProductRequest.prodDescription">
    </div>
    <div class="mb-3">
        <label for="inputPrice" class="form-label">
            Precio actual
        </label>
        <input type="number" class="form-control" id="inputPrice"
        name="Price" [(ngModel)]="addProductRequest.prodPrice" >
    </div>
    <div class="mb-3">
        <label for="inputStock" class="form-label">
            Cantidades disponibles
        </label>
        <input type="number" class="form-control" id="inputStock"
        name="Stock" [(ngModel)]="addProductRequest.prodStock">
    </div>
    <div class="mb-3">
        <label for="inputSaleUnit" class="form-label">
            Unidad de venta
        </label>
        <input type="text" class="form-control" id="inputSaleUnit"
        name="SaleUnit" [(ngModel)]="addProductRequest.prodSaleUnit" >
    </div>
    <button type="submit" class="btn btn-success me-2">Agregar</button>
    <button class="btn btn-secondary"  [routerLink]="['/listview']">Volver</button>
</form>
